import React from "react";
import { useModal } from "../hooks/useModal";
import { Modal } from "./Modal";

export const Modals = ({detailsImage}) => {
  const [isOpenModal, openModal, closeModal] = useModal(false);
  return (
    //     <img
    //     classname="subColumna2Icons"
    //     src={detailsImage}
    //     alt={"details type"}
    //   />
    <div>
      <h2>modals</h2>
      <img
        onClick={openModal}
        classname="subColumna2Icons"
        src={detailsImage}
        alt={"details type"}
      />
      <Modal isOpen={isOpenModal} closeModal={closeModal}>
      <div className="topBoxModalInfo">
            <input
              className="input"
              ref={pokemonInput}
              type="text"
              placeholder="Type here your pokemon"
            ></input>
            <img classname="topIcon" src={homeImage} alt={"home search"} />
          </div>
        <div className="infomodal">
          <div className="infomodal column-1">
            <p>aqui va la imagen</p>
          </div>
          <div className="infomodal column-2">
            <p>aqui van las caracteristicas</p>
          </div>
          <button class="modal-close" onClick={closeModal}>x</button>
        </div>
      </Modal>
    </div>
  );
};
