import { useEffect, useState, useRef } from "react";
import { Fragment } from "react";
import { getPokemons } from "./services/fetchinPokeapi"
import './App.css';


function App() {
  const [pokemonsList, setpokemonsList] = useState([]);
  const [pokemonsCaught, setpokemonsCaught] = useState([]);
  useEffect(() => {
    let mounted = true;
    getPokemons()
      .then(items => {
        if(mounted) {
          setpokemonsList(items)
        }
      })
    return () => mounted = false;
  }, [])

  const pokemonRef = useRef()
  const findPokemon = () => {
    const pokemonRefInput = pokemonRef.current.value
    if (pokemonRefInput === '') return;

    for (let index = 0; index < pokemonsList.length; index++) {
      const element = pokemonsList[index];
      if (element.name == pokemonRefInput) {
        return "el pokemon existe"
      }
      return "No hay pokemon"
    }

  }


  return (
  <Fragment>
    <h1>Pokemon ... atrapalos ya</h1>
    <h2>Pokemons to catch {pokemonsList.length}</h2>
    <div className="container">
      <ul>
       {pokemonsList.map((item, pos) => <li key={item.name}><strong>{pos} </strong>{item.name}</li>)}
     </ul>
    </div>
    <h2>Pokemons caught = {pokemonsCaught.length}</h2>
    <input ref="pokemonRef"type="text" placeholder="Type here your pokemon"></input>
    <button>capture</button>

  </Fragment>
  );
}

export default App;
