import React from "react";
import { useModal } from "../hooks/useModal";
import { Modal } from "./Modal";
import exitImage from "../images/xicon.JPG";

export const Modals = ({ detailsImage }) => {
  const [isOpenModal, openModal, closeModal] = useModal(false);
  return (
    //     <img
    //     classname="subColumna2Icons"
    //     src={detailsImage}
    //     alt={"details type"}
    //   />
    <div>
      <img
        onClick={openModal}
        classname="subColumna2Icons"
        src={detailsImage}
        alt={"details type"}
      />
      <Modal isOpen={isOpenModal}>
        <div className="infomodal">
          <div className="topBoxModalInfo">
            <img
              onClick={closeModal}
              classname="subColumna2Icons"
              src={exitImage}
              alt={"exit type"}
            />
            <input
              className="input"
              //   ref={pokemonInput}
              type="text"
              placeholder="Type here your pokemon"
            ></input>
          </div>
          <div className="infomodalAll">
          <div className="infomodal column-1">
            <p>aqui va la imagen</p>
          </div>
          <div className="infomodal column-2">
            <p>aqui van las caracteristicas</p>
          </div>
          </div>
        </div>
      </Modal>
    </div>
  );
};
